import React from 'react';
import './sendMessages.css';


function sendMessages(props) {
    const [messageText, defText] = useState("");
    function send() {
        props.text(messageText);
        defText("");
      }

    function onKeyPress(keyPress) {
        if (keyPress.key === "Enter") {
          send();
        }
      }

    return (

        <footer className="app-footer">
            <input className="text-input"
            value={props.text}
            onChange={(keyPress) => defText(keyPress.target.value)}
            onKeyPress={onKeyPress}
            />
            <button className="send-button" onClick = {send}>&#x21E7;</button>
        </footer>
    );
}

export default sendMessages;
