import './messages.css';


function DispMessage(props) {
    messageArr = props.Messages;

    function Message(props) {
        // add the date
        // add the username
        // add a pic
        return <div className="message">{props.text}}</div>;
    }

    return (
        <div className = "messages">
            {messageArr.map((msg) => {
                return <Message {...msg} />;
            })}
        </div>
    )
}

export default DispMessage;
